// src/pages/AdminPage/SubmissionPage.jsx
import React, { useState } from 'react';
import AdminLayout from '../../layouts/AdminLayout';
import styles from './SubmissionPage.module.css';

const departments = [
    '학적 | 학사지원과',
    '등록 | 재무과',
    '수업 | 학사지원과',
    '학생 | 학생과',
];

const dummyData = [
    { id: 1, title: '공인영어능력 인정 신청서', submitDate: '2025.04.25', status: '완료', department: '학적 | 학사지원과' },
    { id: 2, title: '등록금 환불 신청서', submitDate: '2025.04.24', status: '미제출', department: '등록 | 재무과' },
    { id: 3, title: '수강포기 신청서', submitDate: '2025.04.23', status: '검토중', department: '수업 | 학사지원과' },
    { id: 4, title: '봉사활동 확인서', submitDate: '2025.04.22', status: '완료', department: '학생 | 학생과' },
];

const SubmissionPage = () => {
    const [selectedDept, setSelectedDept] = useState(departments[0]);

    const filteredData = dummyData.filter(item => item.department === selectedDept);

    return (
        <AdminLayout>
            <div className={styles.page}>
                <div className={styles.sidebar}>
                    <h3 className={styles.menuTitle}>행정 서비스</h3>
                    <ul>
                        {departments.map(dept => (
                            <li
                                key={dept}
                                onClick={() => setSelectedDept(dept)}
                                className={selectedDept === dept ? styles.active : ''}
                            >
                                {dept}
                            </li>
                        ))}
                    </ul>
                </div>

                <div className={styles.content}>
                    <div className={styles.selectedLabel}>▶ {selectedDept}</div>

                    <table className={styles.table}>
                        <thead>
                        <tr>
                            <th>서류 제목</th>
                            <th>제출일자</th>
                            <th>상태</th>
                        </tr>
                        </thead>
                        <tbody>
                        {filteredData.map(doc => (
                            <tr key={doc.id}>
                                <td>{doc.title}</td>
                                <td>{doc.submitDate}</td>
                                <td>{doc.status}</td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>
            </div>
        </AdminLayout>
    );
};

export default SubmissionPage;
