// 파일명: SubmissionPage.jsx
import React, { useState } from 'react';
import styles from './SubmissionPage.module.css';

const departments = ['학적 | 학사지원과', '등록 | 재무과', '수업 | 학사지원과', '학생 | 학생과'];

const mockData = {
    '학적 | 학사지원과': [
        { title: '공인영어능력 인정 신청서', date: '2025.04.25', status: '완료' }
    ],
    '등록 | 재무과': [
        { title: '등록금 환불 신청서', date: '2025.04.21', status: '미완료' }
    ],
    '수업 | 학사지원과': [],
    '학생 | 학생과': []
};

const SubmissionPage = () => {
    const [selectedDept, setSelectedDept] = useState('학적 | 학사지원과');

    return (
        <div className={styles.page}>
            <div className={styles.departmentList}>
                <h3>행정 서비스</h3>
                <ul>
                    {departments.map((dept) => (
                        <li
                            key={dept}
                            className={dept === selectedDept ? styles.active : ''}
                            onClick={() => setSelectedDept(dept)}
                        >
                            ▶ {dept}
                        </li>
                    ))}
                </ul>
            </div>

            <div className={styles.documentSection}>
                <h3 className={styles.selectedDept}>▶ {selectedDept}</h3>
                <table className={styles.table}>
                    <thead>
                    <tr>
                        <th>서류 제목</th>
                        <th>제출일자</th>
                        <th>상태</th>
                    </tr>
                    </thead>
                    <tbody>
                    {mockData[selectedDept].length > 0 ? (
                        mockData[selectedDept].map((doc, i) => (
                            <tr key={i}>
                                <td>{doc.title}</td>
                                <td>{doc.date}</td>
                                <td>{doc.status}</td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="3" style={{ textAlign: 'center' }}>
                                제출된 서류가 없습니다.
                            </td>
                        </tr>
                    )}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default SubmissionPage;
