import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

import styles from './Sidebar.module.css';

const Sidebar = () => {
    const navigate = useNavigate();

    const handleClick = (categoryKey) => {
        // 원하는 경로에 맞춰 이동
        navigate(`/admin/submissions?category=${categoryKey}`);
    };

    return (
        <aside className={styles.sidebar}>
            <div className={styles.title}>제출 서류 목록</div>
            <hr className={styles.divider} />
            <ul className={styles.menu}>
                <li onClick={() => handleClick('academic')} className={styles.menuItem}>▶ 학적 | 학사지원과</li>
                <li onClick={() => handleClick('finance')} className={styles.menuItem}>▶ 등록 | 재무과</li>
                <li onClick={() => handleClick('class')} className={styles.menuItem}>▶ 수업 | 학사지원과</li>
                <li onClick={() => handleClick('student')} className={styles.menuItem}>▶ 학생 | 학생과</li>
            </ul>
        </aside>
    );
};


export default Sidebar;
