import React, { useState } from 'react';
import Header from '../../components/Header';
import Sidebar from '../../components/Sidebar';
import styles from './StudentMain.module.css';

const StudentMain = () => {
    const [selectedMenu, setSelectedMenu] = useState('제출 서류 목록');

    const renderContent = () => {
        switch (selectedMenu) {
            case '학적':
                return (
                    <section className={styles.documentList}>
                        <h3>학적 | 행사지원과 제출 서류</h3>
                        <ul>
                            {[...Array(5)].map((_, idx) => (
                                <li key={idx} className={styles.documentItem}>
                                    서류.hwp | 서류 제출 기한(yyyy.mm.dd ~ yyyy.mm.dd)
                                    <br />
                                    필수 항목: 성명, 날짜, 이메일 등 ... (전화)
                                </li>
                            ))}
                        </ul>
                    </section>
                );
            default:
                return (
                    <>
                        <section className={styles.profileCard}>
                            <img src="/images/cnu_logo.png" alt="프로필" className={styles.logo} />
                            <div className={styles.name}>홍길동</div>
                            <div className={styles.info}>화학과(학부생) | 20100000</div>
                        </section>
                        <section className={styles.chatbotSection}>
                            <p className={styles.guideText}>
                                챗봇 지원 시스템은 <strong>CNU 챗봇</strong>과 함께합니다!
                            </p>
                            <button className={styles.chatbotButton}>CNU 챗봇</button>
                        </section>
                    </>
                );
        }
    };

    return (
        <div className={styles.container}>
            <Sidebar onMenuClick={setSelectedMenu} />
            <div className={styles.mainContent}>
                {renderContent()}
            </div>
        </div>
    );
};

export default StudentMain;
