import React from 'react';
import { useOutletContext } from 'react-router-dom';
import styles from './StudentMain.module.css';

const StudentMain = () => {
    const { selectedMenu } = useOutletContext();

    const renderInnerBox = () => {
        switch (selectedMenu) {
            case '학적':
                return (
                    <div className={styles.innerContent}>
                        <p><strong>▶ 학적 | 학사지원과 제출 서류</strong></p>
                        {[...Array(5)].map((_, idx) => (
                            <div key={idx} className={styles.documentItem}>
                                서류.hwp | 제출 기한 yyyy.mm.dd ~ yyyy.mm.dd<br />
                                필수 항목: 성명, 날짜, 이메일 등... (학과)
                            </div>
                        ))}
                    </div>
                );
            case '등록':
                return (
                    <div className={styles.innerContent}>
                        <p><strong>▶ 등록 | 재무과 제출 서류</strong></p>
                        <div className={styles.documentItem}>등록서류.hwp | yyyy.mm.dd<br />필수 항목: 성명, 학번, 연락처</div>
                    </div>
                );
            case '수업':
                return (
                    <div className={styles.innerContent}>
                        <p><strong>▶ 수업 | 학사지원과 제출 서류</strong></p>
                        <div className={styles.documentItem}>출석확인서.hwp 등</div>
                    </div>
                );
            case '학생':
                return (
                    <div className={styles.innerContent}>
                        <p><strong>▶ 학생 | 학생과 제출 서류</strong></p>
                        <div className={styles.documentItem}>상담확인서.hwp 등</div>
                    </div>
                );
            default:
                return (
                    <div className={styles.innerContentEmpty}>
                        제출 지원 서류 목록
                    </div>
                );
        }
    };

    return (
        <div className={styles.mainContent}>
            <section className="profileCard">
                <div className="headerRow">
                    <img className="logo" />
                    <div className="userInfoBox">
                        <div className="name">홍길동</div>
                        <div className="info">화학과(재학생) | 20100000</div>
                    </div>
                </div>
                <div className="sectionTitle">제출 지원 서류 목록</div>
                <div className="innerContent"> 
                    {renderInnerBox()}
                </div>
            </section>


            <section className={styles.chatbotSection}>
                <p className={styles.guideText}>
                    챗봇 지원 시스템은 <strong>CNU 챗봇</strong>과 함께합니다!
                </p>
                <button className={styles.chatbotButton}>CNU 챗봇</button>
            </section>
        </div>
    );
};

export default StudentMain;
