import React, { useState } from 'react';
import AdminLayout from '../../layouts/AdminLayout';
import Sidebar from '../../components/SubmissionSidebar';
import SubmissionContent from '../../components/SubmissionContent';
import styles from './SubmissionPage.module.css';

const departments = ['학적 | 학사지원과', '등록 | 재무과', '수업 | 학사지원과', '학생 | 학생과'];

const mockData = {
    '학적 | 학사지원과': [
        { title: '공인영어능력 인정 신청서', date: '2025.04.25', status: '완료' }
    ],
    '등록 | 재무과': [
        { title: '등록금 환불 신청서', date: '2025.04.21', status: '미완료' }
    ],
    '수업 | 학사지원과': [],
    '학생 | 학생과': []
};

const SubmissionPage = () => {
    const [selectedDept, setSelectedDept] = useState(departments[0]);

    return (
        <AdminLayout>
            <div className={styles.page}>
                <Sidebar
                    departments={departments}
                    selectedDept={selectedDept}
                    onSelectDept={setSelectedDept}
                />
                <SubmissionContent
                    selectedDept={selectedDept}
                    data={mockData[selectedDept]}
                />
            </div>
        </AdminLayout>
    );
};

export default SubmissionPage;
