import { useEffect, useState } from 'react';
import AdminLayout from '../../layouts/AdminLayout';
import SubmissionStatusCard from '../../components/AdminMain/SubmissionStatusCard';
import SubmissionDeadlineCard from '../../components/AdminMain/SubmissionDeadlineCard';

const AdminMain = () => {
    const [statusData, setStatusData] = useState([]);
    const [deadlineData, setDeadlineData] = useState([]);
    const [loading, setLoading] = useState(true);

    const timestamp = "2025-04-25 (금) 오후 5:01 기준"; // 추후 자동화 가능

    useEffect(() => {
        // 더미 API 대체
        const fetchData = async () => {
            try {
                // 예: const status = await fetch('/api/admin/status-summary');
                setStatusData([
                    { department: '학적 | 학사지원과', count: 15 },
                    { department: '등록 | 재무과', count: 35 },
                    { department: '수업 | 학사지원과', count: 15 },
                    { department: '학생 | 학생과', count: 15 },
                ]);
                setDeadlineData([
                    { title: '공인영어능력 인정 신청서', deadline: '0000.00.00', status: '기간 설정' },
                    { title: '공인영어능력 인정 신청서', deadline: '0000.00.00', status: '기간 설정' },
                    { title: '공인영어능력 인정 신청서', deadline: '0000.00.00', status: '기간 설정' },
                    { title: '공인영어능력 인정 신청서', deadline: '0000.00.00', status: '기간 설정' },
                    { title: '공인영어능력 인정 신청서', deadline: '기간 설정 정보 없음', status: '기간 설정' },
                    { title: '공인영어능력 인정 신청서', deadline: '기간 설정 정보 없음', status: '기간 설정' },
                ]);
                setLoading(false);
            } catch (err) {
                console.error("데이터 로딩 실패", err);
            }
        };
        fetchData();
    }, []);

    if (loading) return <AdminLayout><p style={{ padding: '30px' }}>불러오는 중...</p></AdminLayout>;

    return (
        <AdminLayout>
            <div style={{ padding: '40px', maxWidth: '900px', margin: '0 auto' }}>
                <SubmissionStatusCard data={statusData} timestamp={timestamp} />
                <div style={{ height: '40px' }} />
                <SubmissionDeadlineCard data={deadlineData} timestamp={timestamp} />
            </div>
        </AdminLayout>
    );
};

export default AdminMain;
