import React, { useState } from 'react';
import { useOutletContext } from 'react-router-dom';
import styles from './StudentMain.module.css';

const StudentMain = () => {
    const [selectedMenu, setSelectedMenu] = useState('default');

    const renderContent = () => {
        switch (selectedMenu) {
            case '학적':
                return <div>학적 관련 제출 서류 리스트</div>;
            case '등록':
                return <div>등록 관련 제출 서류 리스트</div>;
            // ... 나머지 케이스
            default:
                return (
                    <>
                        <section className={styles.profileCard}>
                            <img src="/images/cnu_logo.png" alt="프로필" className={styles.logo} />
                            <div className={styles.name}>홍길동</div>
                            <div className={styles.info}>화학과(학부생) | 20100000</div>
                        </section>
                        <section className={styles.chatbotSection}>
                            <p className={styles.guideText}>
                                챗봇 지원 시스템은 <strong>CNU 챗봇</strong>과 함께합니다!
                            </p>
                        </section>
                    </>
                );
        }
    };

    return (
        <div className={styles.container}>
            <div className={styles.mainContent}>
                {renderContent()}
            </div>
        </div>
    );
};

export default StudentMain;
