import React from 'react';
import styles from './Chatbot.module.css';
import ChatBubble from './ChatBubble';
import ChatOptions from './ChatOptions';
import FileUploader from './FileUploader';
import useChatScenario from "../../hooks/useChatScenario";

const Chatbot = ({ onClose }) => {

    const {
        chatHistory,
        handleUserInput,
        handleFileUpload,
    } = useChatScenario();

    return (
        <div className={styles.chatbotPanel}>
            <div className={styles.chatHeader}>
                <div> CNU 챗봇</div>
                <button className={styles.closeButton} onClick={onClose}>
                    ✕
                </button>
            </div>

            <div className={styles.chatBody}>
                {/* 마스코트 + 메시지 + 옵션 버튼 묶음 */}
                <div className={styles.botGreetingBox}>
                    <img src="/images/chat_mascot.png" alt="챗봇 마스코트" className={styles.botMascot} />

                    <div className={styles.botMessageBox}>
                        <div className={styles.greetingText}>
                            안녕하세요!<br />무엇을 도와드릴까요?
                        </div>

                        <div className={styles.optionsContainer}>
                            <button className={styles.optionButton}>서류 제출</button>
                            <button className={styles.optionButton}>서류 제출 현황</button>
                        </div>
                    </div>
                </div>
            </div>


            <div className={styles.chatInput}>
                <input type="text" placeholder="메시지를 입력하세요." />
                <button>전송</button>
            </div>
        </div>
    );
};

export default Chatbot;
