import React from 'react';
import { useLocation } from 'react-router-dom';
import styles from './SubmissionPage.module.css';

const dummyData = [...Array(50)].map((_, idx) => ({
    id: idx + 1,
    name: `홍길동${idx + 1}`,
    studentId: `202001${(10 + idx).toString().padStart(2, '0')}`,
    fileTitle: `[장학금 신청서_202001${(10 + idx).toString().padStart(2, '0')}_홍길동${idx + 1}].pdf`,
    date: `2025.04.${(1 + idx % 30).toString().padStart(2, '0')}`,
    type: ['신규 등록', '반려 처리', '승인 완료'][idx % 3],
    approved: idx % 3 === 2 ? 'Y' : 'N'
}));


// URL category → 실제 부서명 맵핑
const categoryMap = {
    academic: '학적 | 학사지원과',
    finance: '등록 | 재무과',
    class: '수업 | 학사지원과',
    student: '학생 | 학생과',
};

const SubmissionPage = () => {
    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const category = searchParams.get('category');
    const selectedDept = categoryMap[category] || '학적 | 학사지원과';

    const [page, setPage] = useState(1);
    const [search, setSearch] = useState('');
    const [typeFilters, setTypeFilters] = useState([]);
    const [approvedFilter, setApprovedFilter] = useState(null);

    const filtered = dummyData.filter(item => {
        const matchesSearch = item.fileTitle.includes(search);
        const matchesType = typeFilters.length === 0 || typeFilters.includes(item.type);
        const matchesApproved = approvedFilter === null || (approvedFilter === 'Y' ? item.approved === 'Y' : item.approved === 'N');
        return matchesSearch && matchesType && matchesApproved;
    });

    const pageSize = 10;
    const paged = filtered.slice((page - 1) * pageSize, page * pageSize);
    const totalPages = Math.ceil(filtered.length / pageSize);

    return (
        <div className={styles.page}>
            <div className={styles.documentSection}>
                <h3 className={styles.selectedDept}>▶ {selectedDept}</h3>
                <table className={styles.table}>
                    <thead>
                    <tr>
                        <th>서류 제목</th>
                        <th>제출일자</th>
                        <th>상태</th>
                    </tr>
                    </thead>
                    <tbody>
                    {mockData[selectedDept].length > 0 ? (
                        mockData[selectedDept].map((doc, i) => (
                            <tr key={i}>
                                <td>{doc.title}</td>
                                <td>{doc.date}</td>
                                <td>{doc.status}</td>
                            </tr>
                        ))
                    ) : (
                        <tr>
                            <td colSpan="3" style={{ textAlign: 'center' }}>
                                제출된 서류가 없습니다.
                            </td>
                        </tr>
                    )}
                    </tbody>
                </table>
            </div>
        </div>
    );
};

export default SubmissionPage;
