import React from 'react';
import { useOutletContext } from 'react-router-dom';
import styles from './StudentMain.module.css';

const StudentMain = () => {
    const { selectedMenu } = useOutletContext();

    const MENU_TITLES = {
        학적: '▶ 학적 | 학사지원과 제출 서류',
        등록: '▶ 등록 | 재무과 제출 서류',
        수업: '▶ 수업 | 학사지원과 제출 서류',
        학생: '▶ 학생 | 학생과 제출 서류',
    };

    
    const renderInnerBox = () => {
        switch (selectedMenu) {
            case '학적':
                return (
                    <>
                        <div className={styles.documentItem}>
                            서류.hwp | 제출 기한 yyyy.mm.dd ~ yyyy.mm.dd<br />
                            필수 항목: 성명, 날짜, 이메일 등... (학과)
                        </div>
                        <div className={styles.documentItem}>
                            서류.hwp | 제출 기한 yyyy.mm.dd ~ yyyy.mm.dd<br />
                            필수 항목: 성명, 날짜, 이메일 등... (학과)
                        </div>
                    </>
                );
            case '등록':
                return <div className={styles.documentItem}>등록서류.hwp | yyyy.mm.dd</div>;
            case '수업':
                return <div className={styles.documentItem}>출석확인서.hwp 등</div>;
            case '학생':
                return <div className={styles.documentItem}>상담확인서.hwp 등</div>;
            default:
                return <div className={styles.placeholderText}>해당 부서를 선택해주세요.</div>;
        }
    };

    return (
        <div className={styles.mainContent}>
            <section className={styles.profileCard}>
                <div className={styles.headerRow}>
                    <img src="/images/logo.png" alt="프로필" className={styles.logo} />
                    <div className={styles.userInfoBox}>
                        <div className={styles.name}>홍길동</div>
                        <div className={styles.info}>화학과(재학생) | 20100000</div>
                    </div>
                </div>

                <div className={styles.sectionTitle}>제출 지원 서류 목록</div>

                <div className={styles.innerContent}>
                    {renderInnerBox()}
                </div>
            </section>

            <section className={styles.chatbotSection}>
                <p className={styles.guideText}>
                    챗봇 지원 시스템은 <strong>CNU 챗봇</strong>과 함께합니다!
                </p>
                <button className={styles.chatbotButton}>CNU 챗봇</button>
            </section>
        </div>
    );
};

export default StudentMain;
