import React from 'react';
import styles from './Chatbot.module.css';
import ChatBubble from './ChatBubble';
import useChatScenario from '../../hooks/useChatScenario'; // 위치에 맞게 조정
import FileUploader from './FileUploader';

const Chatbot = ({ onClose }) => {
    const {
        chatHistory,
        handleUserInput,
        handleFileUpload,
    } = useChatScenario();

    return (
        <div className={styles.chatbotPanel}>
            <div className={styles.chatHeader}>
                <div> CNU 챗봇</div>
                <button className={styles.closeButton} onClick={onClose}>✕</button>
            </div>

            <div className={styles.chatBody}>
                {chatHistory.map((chat, index) => (
                    <div key={index}>
                        <ChatBubble sender={chat.from} message={chat.message} />
                        {chat.options && (
                            <div className={styles.optionsContainer}>
                                {chat.options.map((opt, i) => (
                                    <button
                                        key={i}
                                        className={styles.optionButton}
                                        onClick={() => handleUserInput(opt)}
                                    >
                                        {opt}
                                    </button>
                                ))}
                            </div>
                        )}
                        {chat.uploadEnabled && (
                            <FileUploader onUpload={handleFileUpload} />
                        )}
                    </div>
                ))}
            </div>

            <div className={styles.chatInput}>
                <input type="text" placeholder="메시지를 입력하세요." disabled />
                <button disabled>전송</button>
            </div>
        </div>
    );
};

export default Chatbot;
