import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import styles from './SubmissionPage.module.css';

const dummyData = [...Array(50)].map((_, idx) => ({
    id: idx + 1,
    name: `홍길동${idx + 1}`,
    studentId: `202001${(10 + idx).toString().padStart(2, '0')}`,
    fileTitle: `[장학금 신청서_202001${(10 + idx).toString().padStart(2, '0')}_홍길동${idx + 1}].pdf`,
    date: `2025-04-${(1 + idx % 30).toString().padStart(2, '0')}`,
    type: ['신규 등록', '반려 처리', '승인 완료'][idx % 3],
    approved: idx % 3 === 2 ? 'Y' : 'N'
}));

const categoryMap = {
    academic: '학적 | 학사지원과',
    finance: '등록 | 재무과',
    class: '수업 | 학사지원과',
    student: '학생 | 학생과',
};

const SubmissionPage = () => {
    const location = useLocation();
    const searchParams = new URLSearchParams(location.search);
    const category = searchParams.get('category');
    const selectedDept = categoryMap[category] || '학적 | 학사지원과';

    const [page, setPage] = useState(1);

    const [searchField, setSearchField] = useState('fileTitle');
    const [searchValue, setSearchValue] = useState('');
    const [startDate, setStartDate] = useState('');
    const [endDate, setEndDate] = useState('');
    const [typeFilters, setTypeFilters] = useState([]);
    const [approvedFilter, setApprovedFilter] = useState(null);
    const [filtered, setFiltered] = useState(dummyData);

    const pageSize = 10;
    const totalPages = Math.ceil(filtered.length / pageSize);
    const paged = filtered.slice((page - 1) * pageSize, page * pageSize);

    const handleSearch = () => {
        let result = [...dummyData];

        // 제목/이름 검색
        if (searchValue) {
            result = result.filter(item =>
                item[searchField].includes(searchValue)
            );
        }

        // 날짜 필터링
        if (startDate) {
            result = result.filter(item => item.date >= startDate);
        }
        if (endDate) {
            result = result.filter(item => item.date <= endDate);
        }

        // 구분 필터링
        if (typeFilters.length > 0) {
            result = result.filter(item => typeFilters.includes(item.type));
        }

        // 승인 여부 필터링
        if (approvedFilter !== null) {
            result = result.filter(item => item.approved === approvedFilter);
        }

        setFiltered(result);
        setPage(1); // 검색 시 페이지 초기화
    };

    const toggleTypeFilter = (type) => {
        setTypeFilters(prev =>
            prev.includes(type) ? prev.filter(t => t !== type) : [...prev, type]
        );
    };

    return (
        <div className={styles.page}>
            <h3 className={styles.selectedDept}>▶ {selectedDept}</h3>

            <div className={styles.filterSection}>
                <div className={styles.leftFilter}>
                    <div className={styles.row}>
                        <label>조건검색</label>
                        <select value={searchField} onChange={e => setSearchField(e.target.value)}>
                            <option value="fileTitle">서류 제목</option>
                            <option value="name">이름</option>
                        </select>
                        <input value={searchValue} onChange={e => setSearchValue(e.target.value)} />
                    </div>
                    <div className={styles.row}>
                        <label>서류 제출일</label>
                        <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} />
                        <span> ~ </span>
                        <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} />
                    </div>
                </div>

                <div className={styles.rightFilter}>
                    <div className={styles.row}>
                        <label>서류 구분</label>
                        {['신규 등록', '반려 처리', '승인 완료'].map(type => (
                            <label key={type}>
                                <input
                                    type="checkbox"
                                    checked={typeFilters.includes(type)}
                                    onChange={() => toggleTypeFilter(type)}
                                /> {type}
                            </label>
                        ))}
                    </div>
                    <div className={styles.row}>
                        <label>승인 항목</label>
                        <label><input type="radio" checked={approvedFilter === 'Y'} onChange={() => setApprovedFilter('Y')} /> Y</label>
                        <label><input type="radio" checked={approvedFilter === 'N'} onChange={() => setApprovedFilter('N')} /> N</label>
                        <label><input type="radio" checked={approvedFilter === null} onChange={() => setApprovedFilter(null)} /> 전체</label>
                    </div>
                    <button className={styles.searchButton} onClick={handleSearch}>검색</button>
                </div>
            </div>

            <div className={styles.tableWrapper}>
                <table className={styles.table}>
                    <thead>
                    <tr>
                        <th>번호</th><th>이름</th><th>학번</th><th>서류 제목</th><th>서류 제출일자</th><th>구분</th>
                    </tr>
                    </thead>
                    <tbody>
                    {paged.map((doc) => (
                        <tr key={doc.id}>
                            <td>{doc.id}</td>
                            <td>{doc.name}</td>
                            <td>{doc.studentId}</td>
                            <td>{doc.fileTitle}</td>
                            <td>{doc.date}</td>
                            <td className={doc.type === '승인 완료' ? styles.approved : ''}>{doc.type}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
            </div>

            <div className={styles.pagination}>
                {Array.from({ length: totalPages }, (_, idx) => (
                    <button key={idx} onClick={() => setPage(idx + 1)} className={page === idx + 1 ? styles.active : ''}>
                        {idx + 1}
                    </button>
                ))}
            </div>
        </div>
    );
};

export default SubmissionPage;
